import React, {useState, useEffect, useRef} from 'react';
import Canvas from './Canvas';
import Pencil from './tools/Pencil';
import Pusher from 'pusher-js';
import pushid from 'pushid';
import axios from 'axios';
import {withStyles, createStyles} from '@material-ui/styles';
import Box from '@material-ui/core/Box';
import Container from '@material-ui/core/Container';
import KeyboardEventHandler from 'react-keyboard-event-handler';

const PixelEditor = (props) => { 
  const classes = props.classes;
  const [id, setId] = useState('');
  const [editor, setEditor] = useState(null);
  const editorRef = useRef(null);
  const pusher = new Pusher("8367995d3f0fa627b261", {
    cluster: "us3",
    forceTLS: true
  });
  const channel = pusher.subscribe("editor");

  useEffect(() => {
    if (editorRef.current) {
      setEditor(new Canvas(editorRef.current, 64, 64, new Pencil('ffffff')));
    }
    setId(pushid);
  }, []);

  const syncUpdates = () => {
    channel.bind("code-update", data => {
      if (data.id === id) return;
      editor.set(data.pixels);
    });
    const data = { 
      id: id,
      pixels: editor.pixels,
    };
    axios
    .post("/update-editor", data)
    .catch(err => {
      console.log(err);
    });
  };

  return (
    <Box className={classes.backgroundContainer}>
      <KeyboardEventHandler
      handleKeys={['p']}
      onKeyEvent={() => {editor.tool = new Pencil('#000000')}} />
      <KeyboardEventHandler
      handleKeys={['e']}
      onKeyEvent={() => {if (editor) editor.tool = new Pencil() }} />
      <KeyboardEventHandler
      handleKeys={['meta+z']}
      onKeyEvent={() => {if (editor) editor.undo()}} />
      <KeyboardEventHandler
      handleKeys={['meta+shift+z']}
      onKeyEvent={() => {if (editor) editor.redo()}} />
      <Box className={classes.canvas} border={1}>
        <canvas onClick={() => {
          syncUpdates();
          }} ref={editorRef} />
      </Box>
      <button onClick={() => { if (editor) editor.tool = new Pencil() }}>Eraser</button>
      <button onClick={() => { if (editor) editor.tool = new Pencil('#110000') }}>Pencil</button>
      <button onClick={() => { if (editor) editor.undo() }}>Undo</button>
      <button onClick={() => { if (editor) editor.redo() }}>Redo</button>
    </Box>
    );
}

const useStyles = (theme) => createStyles({
  backgroundContainer: {
    backgroundSize: 'cover',
    background: theme.palette.primary.main,
    overflowX: 'hidden',
 },
  canvas: {
    width: '50%',
    paddingLeft: 0,
    paddingRight: 0,
    marginTop: '1rem',
    marginLeft: 'auto',
    marginRight: 'auto',
    display: 'block'
  }
});

export default withStyles(useStyles)(PixelEditor);
