import {createMuiTheme} from '@material-ui/core';
import PixelArtFont from './fonts/pixelart.woff';

const pixelartfont = {
 fontFamily: 'Pixel-Art Regular',
 fontStyle: 'semi-bold',
 fontDisplay: 'swap',
 fontWeight: '600',
 src: `
   local('Pixel-Art'),
   local('Pixel-Art Regular'),
   url(${PixelArtFont}) format('woff')
 `,
 unicodeRange:
   'U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF',
};
const theme = createMuiTheme({
    typography: {
        fontFamily: ['"Open Sans"', 'Pixel-Art Regular', 'Roboto'].join(','),
    },
    overrides: {
        MuiCssBaseline: {
          '@global': {
            '@font-face': [pixelartfont],
          },
        }
      }
});

export default theme;
