import {createMuiTheme} from '@material-ui/core';


const theme = createMuiTheme({
    palette: {
        primary: {
          main: '#545863',
        },
        secondary: {
          main: '#f96e46',
        },
    },
    typography: {
        fontFamily: 'HardPixel',
        color: '#FFE3E3',
    },
    overrides: {
        typography: { 
          text: { 
            color: '#FFE3E3',
        },
        },
      },
});

export default theme;
