import {createMuiTheme} from '@material-ui/core';
import pixelartfont from './fonts/pixelart.woff';

const pixelart = {
    fontFamily: 'pixelart',
    fontStyle: 'normal',
    fontDisplay: 'swap',
    fontWeight: 400,
    src: `
        local('pixelart'),
        local('pixelart'),
        url(${pixelartfont}) format('woff')
    `,
};

const theme = createMuiTheme({
    typography: {
        fontFamily: [
            'pixelart'
        ].join(','),
    },
    overrides: {
        MuiCssBaseline: {
            '@global': {
                '@font-face': [pixelart],
            }
        }
    }
});

export default theme;
