import React, { Component } from 'react';
import { PixelEditor, Pencil } from '@curtishughes/pixel-editor';
import Pusher from 'pusher-js';
import pushid from 'pushid';
import axios from 'axios';
import { stringify } from 'flatted';

class App extends Component {
    constructor() {
        super();
        this.state = {
          id: '',
          editor : null,
        };
        this.editorRef = React.createRef();
        this.pusher = new Pusher("8367995d3f0fa627b261", {
          cluster: "us3",
          forceTLS: true
        });
        this.channel = this.pusher.subscribe("editor");
    }
    componentDidMount() {
        if (this.editorRef.current) {
            this.setState({
                editor : new PixelEditor(this.editorRef.current, 64, 64, new Pencil('black')),
            }) 
        }
        this.setState({
          id: pushid(),
        });

        this.channel.bind("code-update", data => {
            console.log("hello")
          const { id } = this.state;
          if (data.id === id) return;
            console.log("o?")
          this.editorRef = data.editorRef;
        });
    }

    syncUpdates = () => {
      const data = { 
          id: this.state.id,
          editorRef: stringify(this.editorRef),
      };
        axios
        .post("/update-editor", data)
        .catch(console.error);
    };

    render() {
        const { editor } = this.state;
        return (
            <>
            <canvas onClick={(editorRef) => {
              editorRef.persist();
                this.setState({ editorRef }, () => this.syncUpdates());
              }} ref={this.editorRef} />
                <button onClick={() => { if (editor) editor.tool = new Pencil() }}>Eraser</button>
                <button onClick={() => { if (editor) editor.tool = new Pencil('black') }}>Pencil</button>
                <button onClick={() => { if (editor) editor.undo() }}>Undo</button>
                <button onClick={() => { if (editor) editor.redo() }}>Redo</button>
            </>
        );
    }
}

export default App;
