import React, {useState, useEffect, useRef} from 'react';
import Canvas from './Canvas';
import Pencil from './tools/Pencil';
import Pusher from 'pusher-js';
import pushid from 'pushid';
import axios from 'axios';
import {withStyles, createStyles} from '@material-ui/styles';
import Box from '@material-ui/core/Box';
import Container from '@material-ui/core/Container';
import ToolBox from './components/ToolBox';
import KeyboardEventHandlers from './components/KeyboardEventHandlers';
import cursor from './static/pencil_cursor.png'; 

const PixelEditor = (props) => { 
  const classes = props.classes;
  const [id, setId] = useState('');
  const [currCursor, setCurrCursor] = useState(cursor);
  const [editor, setEditor] = useState(null);
  const editorRef = useRef(null);
  const pusher = new Pusher("8367995d3f0fa627b261", {
    cluster: "us3",
    forceTLS: true
  });
  const channel = pusher.subscribe("editor");

  useEffect(() => {
    if (editorRef.current) {
      setEditor(new Canvas(editorRef.current, 64, 64, new Pencil('#000000')));
    }
    setId(pushid);
  }, []);

  const syncUpdates = () => {
    channel.bind("code-update", data => {
      if (data.id === id) return;
      editor.set(data.pixels);
    });
    const data = { 
      id: id,
      pixels: editor.pixels,
    };
    axios
    .post("/update-editor", data)
    .catch(err => {
      console.log(err);
    });
  };

  return (
    <Box className={classes.backgroundContainer}>
      <KeyboardEventHandlers editor={editor} color={'#000000'}/>
      <ToolBox editor={editor}/>
      <Container className={classes.canvasBox}>
        <canvas style={{cursor: `url(${currCursor}) 10 10, auto`}} className={classes.canvas} onClick={() => {
          syncUpdates();
          }} ref={editorRef} />
      </Container>
    </Box>
    );
}

const useStyles = (theme) => createStyles({
  backgroundContainer: {
    display: 'flex',
    backgroundSize: 'cover',
    background: theme.palette.primary.main,
    overflowX: 'hidden',
  },
  canvasBox: {
    justifyContent: 'center',
  },
  canvas: {
    outline: 'black 3px solid',
    marginTop: '1rem',
    display: 'flex'
  },
});

export default withStyles(useStyles)(PixelEditor);
