import React from 'react';
import Pencil from '../tools/Pencil';
import Line from '../tools/Line';
import Rectangle from '../tools/Rectangle';
import KeyboardEventHandler from 'react-keyboard-event-handler';

const KeyboardEventHandlers = (props) => {
    return (
        <div>
        <KeyboardEventHandler
      handleKeys={['l']}
      onKeyEvent={() => {props.editor.tool = new Line(props.color)}} />
      <KeyboardEventHandler
      handleKeys={['r']}
      onKeyEvent={() => {props.editor.tool = new Rectangle(props.color)}} />
      <KeyboardEventHandler
      handleKeys={['p']}
      onKeyEvent={() => {props.editor.tool = new Pencil(props.color)}} />
      <KeyboardEventHandler
      handleKeys={['e']}
      onKeyEvent={() => {
        if (props.editor) props.editor.tool = new Pencil();
        }} />
      <KeyboardEventHandler
      handleKeys={['meta+z']}
      onKeyEvent={() => {if (props.editor) props.editor.undo()}} />
      <KeyboardEventHandler
      handleKeys={['meta+shift+z']}
      onKeyEvent={() => {if (props.editor) props.editor.redo()}} />
      </div>
    )
};

export default KeyboardEventHandlers;
