import React, {useEffect, useState} from 'react';
import {useLocation, useHistory} from 'react-router-dom';
import axios from 'axios';
import {
    Box,
    Typography,
    Avatar,
} from '@material-ui/core';
import {withStyles, createStyles} from '@material-ui/styles';
const UserHome = (props) => {
    const classes = props.classes;
    const user_id = new URLSearchParams(useLocation().search).get('id');
    const [userSprite, setUserSprite] = useState(0);
    const history = useHistory();
    const spritePaths = {
        0 : '/static/fox.png',
    }
    useEffect(() => {
        const getUser = async() => {
            let response;
            try {
                console.log("This is the user id" + user_id);
                response = await axios.post('/api/user', JSON.stringify({user_id}), {
                    headers: {'Content-Type' : 'application/json'}
                });
            } catch (err) {
                console.log(err);
            }
            if (response) { 
                console.log(response.data.user.spriteid);
                setUserSprite(response.data.user.spriteid);

            }
            history.push(`/user/?id=${user_id}`)
        } 
        getUser();
    }, [])
    return (
        <Box className={classes.backgroundContainer}>
            <Avatar src = '/static/fox.png'/>
        </Box>
    );
}

const useStyles = (theme) => createStyles({
   backgroundContainer: {
       backgroundSize: 'cover',
       background: theme.palette.primary.main,
       overflowX: 'hidden',
    },
   section: {
       display: 'flex',
    },
   logo: {
       padding: '1rem',
    }
});

export default withStyles(useStyles)(UserHome);
