import {withStyles, createStyles} from '@material-ui/styles';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';

const ToolBox = (props) => {
    const classes = props.classes;
    return (
        <Grid className={classes.toolBox}>
            <Button onClick={() => { if (editor) editor.tool = new Pencil() }}>Eraser</Button>
            <Button onClick={() => { if (editor) editor.tool = new Pencil('#110000') }}>Pencil</Button>
            <Button onClick={() => { if (editor) editor.tool = new Line('#110000') }}>Line</Button>
            <Button onClick={() => { if (editor) editor.tool = new Rectangle('#110000') }}>Rectangle</Button>
      </Grid>
    )
};

