import React, {useState, useEffect, useRef} from 'react';
import Canvas from './Canvas';
import Pencil from './tools/Pencil';
import Pusher from 'pusher-js';
import pushid from 'pushid';
import axios from 'axios';
import * as util from 'util' // has no default export


function PixelEditor() {
  const [id, setId] = useState('');
  const [editor, setEditor] = useState(null);
  const editorRef = useRef(null);
  const pusher = new Pusher("8367995d3f0fa627b261", {
    cluster: "us3",
    forceTLS: true
  });
  const channel = pusher.subscribe("editor");

  useEffect(() => {
    if (editorRef.current) {
      setEditor(new Canvas(editorRef.current, 64, 64, new Pencil('black')));
    }
    setId(pushid);
    channel.bind("code-update", data => {
      if (data.id === id) return;
      editor.clear();
      editor.set(data.pixels);
    });
  }, []);

  const syncUpdates = () => {
    const data = { 
      id: id,
      pixels: editor.pixels,
    };
    axios
    .post("/update-editor", data)
    .catch(err => {
      console.log(err);
    });
  };

  return (
    <div>
      <canvas onClick={() => {
        syncUpdates();
        }} ref={editorRef} />
        <button onClick={() => { if (editor) editor.tool = new Pencil() }}>Eraser</button>
        <button onClick={() => { if (editor) editor.tool = new Pencil('black') }}>Pencil</button>
        <button onClick={() => { if (editor) editor.undo() }}>Undo</button>
        <button onClick={() => { if (editor) editor.redo() }}>Redo</button>
    </div>
    );
}

export default PixelEditor;
